mapkey ok ~ Activate `main_dlg_cur` `ProCmdSketDone.sket_r`;\
mapkey(continued) ~ Activate `Odui_Dlg_00` `stdbtn_1`;~ Activate `Odui_Dlg_01` `stdbtn_1`;\
mapkey(continued) ~ Activate `main_dlg_cur` `dashInst0.Done`;

mapkey q1 #done/return;#done/return;#done/return;#done/return;
mapkey q2 #return;#return;#return;#return;
mapkey q3 #Done-return;#Done-return;#Done-return;#Done-return;
mapkey q4 #Done sel;#Done sel;#Done sel;#done;#done;#done;#done;
mapkey q5 #quit refs;#quit del/sup;
mapkey q6 #done;#quit;#done;#quit;#confirm;#done/return;#done;#done;
mapkey q7 #done;#quit;#confirm;#done/return;#done;#done;
mapkey q8 ~ Activate `main_dlg_cur` `ProCmdSketQuit.sket_r`;\
mapkey(continued) ~ FocusIn `UI Message Dialog` `yes`;~ Activate `UI Message Dialog` `yes`;\
mapkey(continued) ~ Activate `Odui_Dlg_01` `stdbtn_2`;~ FocusIn `UI Message Dialog` `yes`;\
mapkey(continued) ~ Activate `UI Message Dialog` `yes`;~ Enter `main_dlg_cur` `dashInst0.Quit`;\
mapkey(continued) ~ Activate `main_dlg_cur` `dashInst0.Quit`;\
mapkey(continued) ~ FocusIn `UI Message Dialog` `yes`;~ Activate `UI Message Dialog` `yes`;

mapkey qq @MAPKEY_NAMEQuit Menus;@MAPKEY_LABELQuit Menus;%q1;%q2;%q3;%q4;%q5;\
mapkey(continued) %q6;%q7;%q8;

mapkey rl @MAPKEY_NAMERelations;@MAPKEY_LABELRelations;\
mapkey(continued) ~ Command `ProCmdMmRels` ;

mapkey ftt @MAPKEY_LABELFamily Table;~ Command `ProCmdMmFamTab`;

mapkey ftv @MAPKEY_LABELFamily Table Verify;~ Command `ProCmdMmFamTab`;\
mapkey(continued) ~ Activate `ftb_edit_table` `TBVerify`;~ Activate `mdlbrowser` `OK`;\
mapkey(continued) ~ Activate `mdlbrowser` `Cancel`;~ Activate `ftb_edit_table` `OK`;\
mapkey(continued) ~ FocusIn `UI Message Dialog` `confirm`;\
mapkey(continued) ~ Activate `UI Message Dialog` `confirm`;~ Activate `mdlbrowser` `Cancel`;\
mapkey(continued) ~ Activate `ftb_edit_table` `OK`;

mapkey ra @MAPKEY_NAMERegen-Auto;@MAPKEY_LABELRegen-Auto;\
mapkey(continued) ~ Command `ProCmdRegenAuto` ;#CURRENT VALS;#CURRENT VALS;#CURRENT VALS;\
mapkey(continued) #CURRENT VALS;

mapkey rc @MAPKEY_NAMERegen-Custom;@MAPKEY_LABELRegen-Custom;\
mapkey(continued) ~ Activate `main_dlg_cur` `Edit.pshRegenCust`;#CURRENT VALS;#CURRENT VALS;\
mapkey(continued) ~ Activate `regenmgr` `Done`;

mapkey re @MAPKEY_NAMERegen-Enter;@MAPKEY_LABELRegen-Enter;\
mapkey(continued) ~ Command `ProCmdRegenPart` ;~ Command `ProCmdRegenAuto` ;#ENTER;

mapkey rd @MAPKEY_NAMERegen Drawing;@MAPKEY_LABELRegen Drawing;\
mapkey(continued) ~ Command `ProCmdDwgRegenModel` ;#AUTOMATIC;#AUTOMATIC;\
mapkey(continued) ~ Command `ProCmdDwgUpdateSheets` ;~ Command `ProCmdDwgRegenDraft` ;

mapkey $F1 @MAPKEY_NAMEProHOW Help;@MAPKEY_LABELProHOW Help;\
mapkey(continued) ~ Activate `main_dlg_cur` `TAB_BROWSER_OPEN_NAME`0 ;\
mapkey(continued) ~ Input `main_dlg_cur` `EMBED_BROWSER URL` `http://intranet/ProHOW`;\
mapkey(continued) ~ Close `main_dlg_cur` `EMBED_BROWSER URL`;\
mapkey(continued) ~ Update `main_dlg_cur` `EMBED_BROWSER URL` `http://intranet/ProHOW`;\
mapkey(continued) ~ Enter `main_dlg_cur` `EMBED_BROWSER URL GO`;\
mapkey(continued) ~ Activate `main_dlg_cur` `EMBED_BROWSER URL GO`;\
mapkey(continued) ~ Exit `main_dlg_cur` `EMBED_BROWSER URL GO`;

mapkey $F7 @MAPKEY_NAMEModify;@MAPKEY_LABELModify;#MODIFY;

mapkey $F8 @MAPKEY_NAMERegen All;@MAPKEY_LABELRegen All;%ra;%rd;

mapkey $F9 @MAPKEY_NAMERevolve: Sketch on pl
