.calendar .days li{color:#fff;background:#999;border-color:#898989;font-size:10px}
.calendar .week{cursor:pointer;z-index:0}.calendar .week.invalid{cursor:default}.calendar .week.invalid li,.calendar .week.invalid:hover li{color:#8c94a4;background:#f1f2f4;border-color:#dcdcdc}
.calendar .week li{padding:4px 0;color:#5a6171;font-size:12px;background:#fff;border-color:#e5e5e5;border-top-width:0}.calendar .week li.outside{color:#8c94a4;background:#f1f2f4;border-color:#dcdcdc}
.calendar .week:hover li{background:#4bb3d6;border-color:#d3dce5}
