.color-editor:focus {
    outline: none;
}
.color-editor aside {
    display: inline-block;
    position: relative;
    height: 100 %;
    overflow: visible;
    margin-left: 6px;
    vertical-align: top;
}
.color-editor aside header {
    width: 100%;
    overflow: hidden;
    margin-bottom: 10px;
}
.color-editor aside header .large-swatches {
    border-radius: 3px;
    background-size: 8px;
    height: 35px;
    width: 88px;
    margin-right: 9px;
    display: inline-block;
    box-shadow: 0 1px 0 #fff;
    position: relative;
}
