//FILE: Vehicle.hh
#ifndef VEHICLE_HH
#define VEHICLE_HH

enum MotorType {
    steam,
    gas,
    diesel,
    jet,
    air
};

class Motor {
    MotorType type;
    double power;
    double strength; //Strength to weight ratio
};

struct Point2d {
    int x, y;
};

struct Dimensions {
    double l, w, h;
};

class Vehicle {
private:
    Motor mtr;
    double weight;
    Dimensions size;
protected:
    double maxSpeed;
    Point2d location;
public:
    Vehicle();
    Vehicle(Motor m, double wt, int maxPass, double maxCarg, Dimensions s, double maxs, Point2d startLoc);
    //Mutators (setters): change/update values
    double move(int deltaX, int deltaY);
    double move(int deltaX, int deltaY, double speed);
    bool fits(Dimensions space);
    //Accessors (getters): get values
    const double getMaxSpeed(); //const as part of the return type means that the user can't change the value returned
    const double getWeight();
    Point2d getLocation();
    const Dimensions getSize();
};

#endif
