/**
 * @ngdoc directive
 * @name app.directives:IidxGauge

 * @element any
 * @function
 * @description
 * This is the description of this directive.
 *
 * > **Note:** You can use Markdown in code comments.
 * @example
  <demo>
<file name="controller.js">
</file>
<file name="index.html">
	<div id="example-html" ng-controller="MainCtrl">
      		<iidx-gauge ng-model="myModel"></iidx-gauge>
    	</div>
</file>
 </demo>
 */
define(['angular', 'vruntime', 'declarative-visualizations'], function (angular, vRuntime, visualizations) {
	'use strict';


	return angular.module('iidx.gauge', []).directive('iidxGauge', function() {
		return {
			scope: {
				title: '@',
				ngModel: '='
			},

			restrict: 'E',
			template: '<div><div data-visualization="gauge" class="chart1" data-value="23" data-max="100" data-threshold="50" data-label-min="0" data-label-max="16mm"></div></div>',
			replace: true,
			link: function(scope, element, attrs){
				$('div[data-visualization="gauge"]').each(function(i, element) {
					var gauge = visualizations.gauge(element);
				});
			}
		};
	});
});
