content:
#category-hero {
    &.apparel {
        background-image: url('../img/asphalt-m.jpg');
    }
    &.collaborations {
        background-image: url('../img/snoop-m.jpg');
    }


}

@media (min-width: 767px) {
    #category-hero {
        &.apparel {
            background-image: url('../img/asphalt.jpg');
        }
        &.collaborations {
            background-image: url('../img/snoop.jpg');
        }

    &.collaborations.claw {
            background-image: url('../img/collaboration-claw.jpg');
    }

    &.collaborations.gumball {
            background-image: url('../img/collaboration-gumball.jpg');
    }

    &.collaborations.huf {
            background-image: url('../img/collaboration-huf.jpg');
    }
    }
}
