Populate(control){
	ea:=xml.ea(control), all:=this.gui.sn("//control[@v]")
	while, aa:=all.Item[A_Index-1], eaa:=xml.ea(aa){
		if (eaa.type~="(ComboBox|DropDownList|DDL|ListBox)"){
			if !(ea[eaa.v])
				GuiControl, % this.win ":", % eaa.hwnd, % eaa.value
			else
				GuiControl, % this.win ":ChooseString", % eaa.hwnd, % ea[eaa.v]
		}else if(eaa.type="Checkbox"){
			GuiControl, % this.win ":", % eaa.hwnd, % ea[eaa.v]!=""?ea[eaa.v]:0
		}else
			GuiControl, % this.win ":", % eaa.hwnd, % ea[eaa.v]!=""?ea[eaa.v]:eaa.value
	}
}
