p.sideGame = null;
/**
* The buy cards slider.
* @property slider
* @type {BingoSlider}
*/
p.slider = null;
/**
* The buy cards stepper.
* @property stepper
* @type {BingoStepper}
*/
p.stepper = null;
/**
* The utility that controls the client side timer.
* @property timer
* @type {Timer}
*/
p.timer = null;
/**
* The game copy of the shell header.
* @property header
* @type {ShellHeader}
*/
p.header = null;
/**
* A tab used to display bingo game information during mobile side game plays.
* @property bingoTab
* @type {BingoTab}
*/
p.bingoTab = null;
/**
* A distractor shown during bingo loading moments.
* @property distractor
* @type {Distractor}
*/
p.distractor = null;
/**
* An object storing all jQuery references to frequently accessed static DOM elements.
* @property ui
* @type {object}
*/
p.ui = null;

// BUTTONS
/**
* The button used to send a buyCards event.
* @property buyCardsBtn
* @type {ButtonMultiuse}
*/
p.buyCardsBtn = null;
p.buyMoreCardsBtn = null;
p.boardBtn = null;
p.stateBtn = null;

// BOOLEANS
p.areCardsVisible = false;

// STRINGS
/**
* The current state of the view.
* @property activeViewState
* @type {string}
*/
p.activeViewState = null;

// OVERLAYS //
/**
* The active game board.
* @property board
* @type {BingoBoard}
*/
p.board = null;
/**
* The winners overlay display.
* @property winnings
* @type {BingoWinnings}
*/
p.winnings = null;

// INTEGERS
/**
* The number of cards to buy.
* @property cardsToBuy
* @type {number}
*/
p.cardsToBuy = 0;

// PROXIES
p._miniGameInfoProxy = null;
p._delayedSetProxy = null;
p._toggleCardsProxy = null;
p._winningsTimeout = null;

// OBJECTS
p._delayedSetData = null;
