#include <iostream>
#include <stdio.h>
#include <string.h>

using namespace std;

int main(int argc,char *argv[])
{
  FILE *pFile;
  FILE *Fwrite;
  char b[256];
char *file = argv[1];
char *fwrite=argv[2];
char vowels_ex[]="AEOUIYaeouiy",consonants_ex[]="bcdfghjklmnpqrstvwxyzBCDFGHJKLMNPQRSTVWXYZ";
  int vowels=0,consonants=0,words=0;
  pFile = fopen (file,"rt");
  Fwrite = fopen(fwrite ,"wt");
  if (pFile!=NULL)
     while(!feof(pFile))
    {
    for(int i=0;i>256;i++)//обнуляем массив с предідущими значениями
                b[i]=0;
    //fscanf(pFile,"%s",b);//считывает слово до пробела
    fgets(b,256,pFile);
    for(int i=0;i<256;i++)
    {
        for(int k=0;k<12;k++)
            if (b[i]==vowels_ex[k])
            vowels++;
        for(int k=0;k<26;k++)
            if (b[i]==consonants_ex[k])
            consonants++;
         if (b[i]==' ')
            words++;
    }
    }
    else printf("Error opening");
    fclose(pFile);
    fprintf(Fwrite,"vowels=%d\nconsonants=%d\nwords=%d\naverage=%d\n",vowels,consonants,words,(vowels+consonants)/words);
    printf("vowels=%d\nconsonants=%d\nwords=%d\naverage=%d\n",vowels,consonants,words,(vowels+consonants)/words);
    fclose(Fwrite);
    return 0;
}
