UPDATE
		 T_BC_ACCESS_CONTROL
		SET MANAGEMENT_NO = #{MANAGEMENTNO} 		
			,ACCESS_EMP_NO = #{ACCESSEMPNO} 		
			,ACCESS_EMP_COMP_CODE = #{ACCESSEMPCOMPCODE} 		
			,REG_DATE = #{REGDATE} 		
			,REG_EMP_NO = #{REGEMPNO} 		
			,REG_EMP_COMP_CODE = #{REGEMPCOMPCODE} 		
			,UPD_DATE = #{UPDDATE} 		
			,UPD_EMP_NO = #{UPDEMPNO} 		
			,UPD_EMP_COMP_CODE = #{UPDEMPCOMPCODE} 		
			,SEQ = #{SEQ} 		
		WHERE MANAGEMENT_NO = #{MANAGEMENTNO}
			AND SEQ = #{SEQ}
