using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Threading.Tasks;

namespace Problem_3
{
    class Program
    {
        static void Main(string[] args)
        {
            var currentLine = Console.ReadLine();
            StringBuilder sb = new StringBuilder();
            List<string> integers = new List<string>();
            List<string> doubles = new List<string>();

            while (currentLine != "//END_OF_CODE")
            {
                sb.AppendLine(currentLine);
                currentLine = Console.ReadLine();
            }
            var text = sb.ToString();
            Regex pattern = new Regex(@"int\s(\w+)|double\s(\w+)");

            MatchCollection matches = pattern.Matches(text);
            foreach (Match match in matches)
            {
                if (match.Groups[2].Value != "")
                {
                    doubles.Add(match.Groups[2].Value);
                }
                if (match.Groups[1].Value != "")
                {
                    integers.Add(match.Groups[1].Value);
                }
            }
            if (doubles.Count == 0)
            {
                Console.WriteLine("Doubles: None");

            }
            else
            {
                Console.WriteLine("Doubles: {0}", string.Join(", ", doubles.OrderBy(d => d)));

            }
            if (integers.Count == 0)
            {
                Console.WriteLine("Ints: None");
            }
            else 
            {
                Console.WriteLine("Ints: {0}", string.Join(", ", integers.OrderBy(i => i)));

            }




        }
    }
}
